let _channel;


function initialize() {
	new QWebChannel( qt.webChannelTransport, channel => {
		_channel = channel;
		window.lightdm = _channel.objects.LightDMGreeter;
		window.greeter_config = _channel.objects.Config;
	});
}


$(window).on('load', initialize);
